import { createClient } from 'npm:@supabase/supabase-js@2.84.0';

interface Job {
  id: string;
  job_name: string;
  last_processed_at: string | null;
  processing_scheduled_for: string | null;
  processing_status: string;
}

interface ChatMessage {
  id: string;
  job_id: string;
  user_id: string;
  message_text: string | null;
  message_type: string;
  image_url: string | null;
  created_at: string;
}

console.info('process-chat-batch function started');

Deno.serve(async (req: Request) => {
  try {
    // Initialize Supabase client with service role key for admin access
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;

    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Starting chat batch processing...');

    // Query jobs that need processing
    const { data: jobsToProcess, error: jobsError } = await supabase
      .from('jobs')
      .select('id, job_name, last_processed_at, processing_scheduled_for, processing_status')
      .eq('processing_status', 'scheduled')
      .lte('processing_scheduled_for', new Date().toISOString());

    if (jobsError) {
      console.error('Error fetching jobs to process:', jobsError);
      return new Response(
        JSON.stringify({ error: 'Failed to fetch jobs', details: jobsError }),
        { status: 500, headers: { 'Content-Type': 'application/json' } }
      );
    }

    if (!jobsToProcess || jobsToProcess.length === 0) {
      console.log('No jobs to process at this time');
      return new Response(
        JSON.stringify({ message: 'No jobs to process', processed: 0 }),
        { status: 200, headers: { 'Content-Type': 'application/json' } }
      );
    }

    console.log(`Found ${jobsToProcess.length} job(s) to process`);

    const results = [];

    // Process each job
    for (const job of jobsToProcess as Job[]) {
      try {
        console.log(`Processing job ${job.id} (${job.job_name})...`);

        // Set status to 'running'
        const { error: updateRunningError } = await supabase
          .from('jobs')
          .update({ processing_status: 'running' })
          .eq('id', job.id);

        if (updateRunningError) {
          console.error(`Error setting job ${job.id} to running:`, updateRunningError);
          throw updateRunningError;
        }

        // Fetch chat messages created after last_processed_at (or all if null)
        let messagesQuery = supabase
          .from('chat_messages')
          .select('*')
          .eq('job_id', job.id)
          .order('created_at', { ascending: true });

        if (job.last_processed_at) {
          messagesQuery = messagesQuery.gt('created_at', job.last_processed_at);
        }

        const { data: messages, error: messagesError } = await messagesQuery;

        if (messagesError) {
          console.error(`Error fetching messages for job ${job.id}:`, messagesError);
          throw messagesError;
        }

        // Edge case: No messages to process
        if (!messages || messages.length === 0) {
          console.log(`No new messages for job ${job.id}, skipping processing`);

          // Reset status back to idle
          await supabase
            .from('jobs')
            .update({ 
              processing_status: 'idle',
              processing_scheduled_for: null
            })
            .eq('id', job.id);

          results.push({
            job_id: job.id,
            job_name: job.job_name,
            status: 'skipped',
            reason: 'no_new_messages',
            messages_processed: 0
          });
          continue;
        }

        console.log(`Found ${messages.length} message(s) for job ${job.id}`);

        // Log the messages (placeholder for future AI processing)
        console.log('=== CHAT MESSAGES TO PROCESS ===');
        console.log(`Job: ${job.job_name} (${job.id})`);
        console.log(`Message count: ${messages.length}`);
        console.log('Messages:');
        (messages as ChatMessage[]).forEach((msg, index) => {
          console.log(`  [${index + 1}] ${msg.created_at} - User ${msg.user_id}:`);
          console.log(`      Type: ${msg.message_type}`);
          if (msg.message_text) {
            console.log(`      Text: ${msg.message_text}`);
          }
          if (msg.image_url) {
            console.log(`      Image: ${msg.image_url}`);
          }
        });
        console.log('=== END CHAT MESSAGES ===');

        // Update job: set last_processed_at, status to idle, clear scheduled_for
        const { error: updateCompleteError } = await supabase
          .from('jobs')
          .update({
            last_processed_at: new Date().toISOString(),
            processing_status: 'idle',
            processing_scheduled_for: null
          })
          .eq('id', job.id);

        if (updateCompleteError) {
          console.error(`Error updating job ${job.id} after processing:`, updateCompleteError);
          throw updateCompleteError;
        }

        console.log(`Successfully processed job ${job.id}`);

        results.push({
          job_id: job.id,
          job_name: job.job_name,
          status: 'success',
          messages_processed: messages.length
        });

      } catch (jobError) {
        console.error(`Error processing job ${job.id}:`, jobError);

        // Set status back to 'scheduled' for retry
        try {
          await supabase
            .from('jobs')
            .update({ processing_status: 'scheduled' })
            .eq('id', job.id);
        } catch (resetError) {
          console.error(`Error resetting job ${job.id} status:`, resetError);
        }

        results.push({
          job_id: job.id,
          job_name: job.job_name,
          status: 'error',
          error: jobError instanceof Error ? jobError.message : String(jobError)
        });
      }
    }

    console.log('Chat batch processing complete');

    return new Response(
      JSON.stringify({
        message: 'Processing complete',
        processed: results.length,
        results
      }),
      { 
        status: 200, 
        headers: { 'Content-Type': 'application/json', 'Connection': 'keep-alive' } 
      }
    );

  } catch (error) {
    console.error('Fatal error in process-chat-batch:', error);
    return new Response(
      JSON.stringify({ 
        error: 'Fatal error', 
        details: error instanceof Error ? error.message : String(error) 
      }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
});

