import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'npm:@supabase/supabase-js@2.84.0';

const SUPABASE_URL = Deno.env.get('SUPABASE_URL');
const SUPABASE_SERVICE_KEY = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY');
const OPENAI_API_KEY = Deno.env.get('OPENAI_API_KEY');

const supabase = createClient(SUPABASE_URL, SUPABASE_SERVICE_KEY);

console.info('process-chat-batch function started');

// STEP 1: CLASSIFIER
async function classifyMessages(messages: any[], jobContext: string) {
  console.log(`Classifying ${messages.length} messages...`);

  const messagesText = messages
    .map((msg, idx) => 
      `[${idx + 1}] (ID: ${msg.id}, User: ${msg.user_id}, Time: ${msg.created_at}) ${msg.message_text || '[Image/Voice message]'}`
    )
    .join('\n');

  const systemPrompt = `You are an AI assistant that analyzes construction/industrial job site chat messages and categorizes them.

Your task is to read chat messages and classify each one into the appropriate categories. A single message can belong to multiple categories.

Categories:
- overview_relevant: Information that affects the high-level job status/summary (be VERY LIBERAL with this - include anything that might be relevant to understanding the job, including issues, questions, progress updates, or any substantive discussion)
- new_issues: Problems, blockers, things that are broken or not working
- resolved_issues: Messages indicating an issue has been fixed or resolved (include the issue description)
- new_questions: Questions that need answers
- answered_questions: Answers to previously asked questions (include both question and answer)
- new_actions: Tasks or action items that need to be done
- completed_actions: Messages indicating a task has been completed
- new_risks: Potential problems, warnings, things that could go wrong
- timeline_changes: Schedule changes, delays, deadline updates, weather impacts, inspection times
- new_dependencies: Things the crew is waiting on (permits, other trades, client approval, equipment)
- cleared_dependencies: Messages indicating a dependency has been resolved
- noise: ONLY casual chat, greetings, off-topic messages with absolutely no actionable content

IMPORTANT: Be liberal with overview_relevant. If a message discusses ANY aspect of the job (issues, progress, questions, problems, etc.), include it in overview_relevant. Only mark as noise if it's truly irrelevant small talk.

For each category, extract:
- message_id: The ID from the message
- text: The original message text
- title: A short title/summary (for issues, actions, risks, dependencies)
- description: Detailed description
- question: The question text (for questions)
- answer: The answer text (for answered questions)
- severity: high/medium/low (for risks)
- priority: high/medium/low (for actions)
- deadline: Any mentioned deadline (for timeline_changes)
- who: Person mentioned if any

Be thorough but precise.`;

  const userPrompt = `Job Context: ${jobContext || 'General construction job'}

Messages to classify:
${messagesText}

Analyze these messages and return a JSON object with the classification results.`;

  const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${OPENAI_API_KEY}`,
    },
    body: JSON.stringify({
      model: 'gpt-4o-mini',
      messages: [
        { role: 'system', content: systemPrompt },
        { role: 'user', content: userPrompt },
      ],
      response_format: { type: 'json_object' },
      temperature: 0.3,
    }),
  });

  if (!openaiResponse.ok) {
    const errorText = await openaiResponse.text();
    throw new Error(`OpenAI API error: ${openaiResponse.status} ${errorText}`);
  }

  const openaiData = await openaiResponse.json();
  const result = JSON.parse(openaiData.choices[0].message.content);

  console.log('Classification result:', JSON.stringify(result, null, 2));

  return {
    overview_relevant: result.overview_relevant || [],
    new_issues: result.new_issues || [],
    resolved_issues: result.resolved_issues || [],
    new_questions: result.new_questions || [],
    answered_questions: result.answered_questions || [],
    new_actions: result.new_actions || [],
    completed_actions: result.completed_actions || [],
    new_risks: result.new_risks || [],
    timeline_changes: result.timeline_changes || [],
    new_dependencies: result.new_dependencies || [],
    cleared_dependencies: result.cleared_dependencies || [],
    noise: result.noise || [],
  };
}

// STEP 2: UPDATERS

// Job Overview Updater - Rewrites the summary while maintaining baseline scope
async function updateJobOverview(
  baselineScope: string | null,
  currentOverview: string | null,
  overviewRelevant: any[],
  allMessages: any[],
  currentState: any,
  jobContext: string
) {
  // If no overview-relevant messages and no baseline, return current
  const messagesToUse = overviewRelevant.length > 0 ? overviewRelevant : allMessages.slice(0, 10);

  if (messagesToUse.length === 0 && !baselineScope) {
    console.log('No messages and no baseline scope to generate overview from');
    return {
      overview: currentOverview || '',
      changes: [],
    };
  }

  console.log(`Updating job overview with ${messagesToUse.length} messages...`);
  console.log('Has baseline scope:', !!baselineScope);

  const systemPrompt = `You are an AI assistant that writes concise, high-level job summaries for construction/industrial projects.

Your task is to write or update a job overview summary based on:
1. The BASELINE scope document (if provided) - this is the foundation and must always be preserved
2. New information from chat messages
3. Current state of the job (issues, questions, actions, etc.)

The summary should:
- Be under 250 words
- Start with the core information from the baseline scope (if provided)
- Integrate updates from chat messages naturally
- Provide a mile-high view of the current job status
- Focus on what matters most: progress, blockers, timeline, key decisions
- Be written in present tense
- Be professional but conversational

This is a REWRITE, not an append. Create a fresh, concise summary that:
- Maintains the essential baseline information from the scope document
- Incorporates the latest updates from chat
- Stays readable and scannable (not a growing list of updates)

If the baseline scope is provided, it should form the core of the overview. Chat updates should enhance it, not replace it.`;

  const userPrompt = `${baselineScope ? `BASELINE SCOPE (must be preserved as foundation):
${baselineScope}

` : ''}Current Overview:
${currentOverview || 'No overview yet.'}

Current Job State:
- Open Issues: ${currentState.open_issues?.length || 0}
- Unanswered Questions: ${currentState.unanswered_questions?.length || 0}
- Next Actions: ${currentState.next_actions?.length || 0}
- Active Risks: ${currentState.warnings_and_risks?.length || 0}
- Dependencies: ${currentState.dependencies?.length || 0}

${messagesToUse.length > 0 ? `New Information from Chat:
${messagesToUse.map((item, idx) => `[${idx + 1}] ${item.text || item.message_text}`).join('\n')}

` : ''}Job Context: ${jobContext}

Write an updated job overview summary (max 250 words) that:
1. Preserves the core baseline scope information (if provided)
2. Integrates the latest chat updates naturally
3. Remains concise and readable
4. Provides a clear current status

${!baselineScope && messagesToUse.length > 0 ? 'Even if the messages seem mundane, create a professional summary of the current job status and any discussions happening.' : ''}`;

  const response = await fetch('https://api.openai.com/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${OPENAI_API_KEY}`,
    },
    body: JSON.stringify({
      model: 'gpt-4o-mini',
      messages: [
        { role: 'system', content: systemPrompt },
        { role: 'user', content: userPrompt },
      ],
      temperature: 0.7,
      max_tokens: 500,
    }),
  });

  if (!response.ok) {
    throw new Error(`Failed to generate overview: ${response.status}`);
  }

  const data = await response.json();
  const newOverview = data.choices[0].message.content.trim();

  console.log('Generated overview:', newOverview);

  const changes = [{
    timestamp: new Date().toISOString(),
    section: 'job_overview',
    change_type: 'updated',
    previous_value: currentOverview,
    new_value: newOverview,
    message_id: messagesToUse[0]?.message_id || messagesToUse[0]?.id,
    message_text: messagesToUse.map(i => i.text || i.message_text).join('; '),
    reason: baselineScope ? 'Integrated chat updates with baseline scope' : 'Updated from chat messages',
  }];

  return { overview: newOverview, changes };
}

// Open Issues Updater
function updateOpenIssues(currentIssues: any[], newIssues: any[], resolvedIssues: any[]) {
  const changes: any[] = [];
  const completedItems: any[] = [];
  let issues = [...(currentIssues || [])];

  // Add new issues
  for (const newIssue of newIssues) {
    const issue = {
      id: crypto.randomUUID(),
      title: newIssue.title || newIssue.text,
      description: newIssue.description || newIssue.text,
      created_at: new Date().toISOString(),
      message_id: newIssue.message_id,
    };
    issues.push(issue);
    changes.push({
      timestamp: new Date().toISOString(),
      section: 'open_issues',
      change_type: 'created',
      new_value: issue,
      message_id: newIssue.message_id,
      message_text: newIssue.text,
      reason: 'New issue identified from chat',
    });
  }

  // Resolve issues
  for (const resolved of resolvedIssues) {
    const issueIndex = issues.findIndex(i => 
      i.title?.toLowerCase().includes(resolved.title?.toLowerCase()) ||
      i.description?.toLowerCase().includes(resolved.description?.toLowerCase())
    );

    if (issueIndex !== -1) {
      const issue = issues[issueIndex];
      issues.splice(issueIndex, 1);

      completedItems.push({
        id: crypto.randomUUID(),
        description: `Resolved: ${issue.title}`,
        source_type: 'issue',
        completed_at: new Date().toISOString(),
        message_id: resolved.message_id,
      });

      changes.push({
        timestamp: new Date().toISOString(),
        section: 'open_issues',
        change_type: 'resolved',
        previous_value: issue,
        new_value: null,
        message_id: resolved.message_id,
        message_text: resolved.text,
        reason: 'Issue marked as resolved',
      });
    }
  }

  return { issues, changes, completedItems };
}

// Unanswered Questions Updater
function updateQuestions(
  currentUnanswered: any[],
  currentResolved: any[],
  newQuestions: any[],
  answeredQuestions: any[]
) {
  const changes: any[] = [];
  let unanswered = [...(currentUnanswered || [])];
  let resolved = [...(currentResolved || [])];

  // Add new questions
  for (const newQ of newQuestions) {
    const question = {
      id: crypto.randomUUID(),
      question: newQ.question || newQ.text,
      asked_at: new Date().toISOString(),
      message_id: newQ.message_id,
    };
    unanswered.push(question);
    changes.push({
      timestamp: new Date().toISOString(),
      section: 'unanswered_questions',
      change_type: 'created',
      new_value: question,
      message_id: newQ.message_id,
      message_text: newQ.text,
      reason: 'New question identified',
    });
  }

  // Answer questions
  for (const answered of answeredQuestions) {
    const qIndex = unanswered.findIndex(q => 
      answered.question && q.question?.toLowerCase().includes(answered.question.toLowerCase())
    );

    if (qIndex !== -1) {
      const question = unanswered[qIndex];
      unanswered.splice(qIndex, 1);

      const resolvedQ = {
        id: question.id,
        question: question.question,
        answer: answered.answer || answered.text,
        asked_at: question.asked_at,
        answered_at: new Date().toISOString(),
        message_id: answered.message_id,
      };
      resolved.push(resolvedQ);

      changes.push({
        timestamp: new Date().toISOString(),
        section: 'unanswered_questions',
        change_type: 'answered',
        previous_value: question,
        new_value: resolvedQ,
        message_id: answered.message_id,
        message_text: answered.text,
        reason: 'Question answered',
      });
    }
  }

  return { unanswered, resolved, changes };
}

// Next Actions Updater
function updateActions(currentActions: any[], newActions: any[], completedActions: any[]) {
  const changes: any[] = [];
  const completedItems: any[] = [];
  let actions = [...(currentActions || [])];

  // Add new actions
  for (const newAction of newActions) {
    const action = {
      id: crypto.randomUUID(),
      title: newAction.title || newAction.text,
      description: newAction.description || newAction.text,
      priority: newAction.priority || 'medium',
      assigned_to: newAction.who,
      created_at: new Date().toISOString(),
      message_id: newAction.message_id,
    };
    actions.push(action);
    changes.push({
      timestamp: new Date().toISOString(),
      section: 'next_actions',
      change_type: 'created',
      new_value: action,
      message_id: newAction.message_id,
      message_text: newAction.text,
      reason: 'New action item identified',
    });
  }

  // Complete actions
  for (const completed of completedActions) {
    const actionIndex = actions.findIndex(a => 
      a.title?.toLowerCase().includes(completed.title?.toLowerCase()) ||
      a.description?.toLowerCase().includes(completed.description?.toLowerCase())
    );

    if (actionIndex !== -1) {
      const action = actions[actionIndex];
      actions.splice(actionIndex, 1);

      completedItems.push({
        id: crypto.randomUUID(),
        description: `Completed: ${action.title}`,
        source_type: 'action',
        completed_at: new Date().toISOString(),
        completed_by: completed.who,
        message_id: completed.message_id,
      });

      changes.push({
        timestamp: new Date().toISOString(),
        section: 'next_actions',
        change_type: 'completed',
        previous_value: action,
        new_value: null,
        message_id: completed.message_id,
        message_text: completed.text,
        reason: 'Action marked as completed',
      });
    }
  }

  return { actions, changes, completedItems };
}

// Warnings & Risks Updater
function updateRisks(currentRisks: any[], newRisks: any[]) {
  const changes: any[] = [];
  let risks = [...(currentRisks || [])];

  // Add new risks
  for (const newRisk of newRisks) {
    const risk = {
      id: crypto.randomUUID(),
      title: newRisk.title || newRisk.text,
      description: newRisk.description || newRisk.text,
      severity: newRisk.severity || 'medium',
      created_at: new Date().toISOString(),
      message_id: newRisk.message_id,
    };
    risks.push(risk);
    changes.push({
      timestamp: new Date().toISOString(),
      section: 'warnings_and_risks',
      change_type: 'created',
      new_value: risk,
      message_id: newRisk.message_id,
      message_text: newRisk.text,
      reason: 'New risk identified',
    });
  }

  return { risks, changes };
}

// Deadlines & Timelines Updater
function updateTimelines(currentTimelines: any[], timelineChanges: any[]) {
  const changes: any[] = [];
  let timelines = [...(currentTimelines || [])];

  for (const change of timelineChanges) {
    const timeline = {
      id: crypto.randomUUID(),
      title: change.title || change.text,
      description: change.description || change.text,
      deadline: change.deadline,
      updated_at: new Date().toISOString(),
      message_id: change.message_id,
    };
    timelines.push(timeline);
    changes.push({
      timestamp: new Date().toISOString(),
      section: 'deadlines_and_timelines',
      change_type: 'created',
      new_value: timeline,
      message_id: change.message_id,
      message_text: change.text,
      reason: 'Timeline update identified',
    });
  }

  return { timelines, changes };
}

// Dependencies Updater
function updateDependencies(
  currentDependencies: any[],
  newDependencies: any[],
  clearedDependencies: any[]
) {
  const changes: any[] = [];
  const completedItems: any[] = [];
  let dependencies = [...(currentDependencies || [])];

  // Add new dependencies
  for (const newDep of newDependencies) {
    const dependency = {
      id: crypto.randomUUID(),
      title: newDep.title || newDep.text,
      description: newDep.description || newDep.text,
      created_at: new Date().toISOString(),
      message_id: newDep.message_id,
    };
    dependencies.push(dependency);
    changes.push({
      timestamp: new Date().toISOString(),
      section: 'dependencies',
      change_type: 'created',
      new_value: dependency,
      message_id: newDep.message_id,
      message_text: newDep.text,
      reason: 'New dependency identified',
    });
  }

  // Clear dependencies
  for (const cleared of clearedDependencies) {
    const depIndex = dependencies.findIndex(d => 
      d.title?.toLowerCase().includes(cleared.title?.toLowerCase()) ||
      d.description?.toLowerCase().includes(cleared.description?.toLowerCase())
    );

    if (depIndex !== -1) {
      const dependency = dependencies[depIndex];
      dependencies.splice(depIndex, 1);

      completedItems.push({
        id: crypto.randomUUID(),
        description: `Cleared: ${dependency.title}`,
        source_type: 'dependency',
        completed_at: new Date().toISOString(),
        message_id: cleared.message_id,
      });

      changes.push({
        timestamp: new Date().toISOString(),
        section: 'dependencies',
        change_type: 'cleared',
        previous_value: dependency,
        new_value: null,
        message_id: cleared.message_id,
        message_text: cleared.text,
        reason: 'Dependency cleared',
      });
    }
  }

  return { dependencies, changes, completedItems };
}

// Main processing function
Deno.serve(async (req) => {
  try {
    console.log('Starting chat batch processing...');

    // Parse request body to check for specific job_id
    let specificJobId = null;
    try {
      const body = await req.json();
      specificJobId = body.job_id || null;
      console.log('Request body:', body);
    } catch {
      // No body or invalid JSON, process all scheduled jobs
    }

    // Query jobs that need processing
    let query = supabase.from('jobs').select('*');

    if (specificJobId) {
      // Manual trigger for specific job
      console.log(`Manual processing requested for job: ${specificJobId}`);
      query = query.eq('id', specificJobId);
    } else {
      // Automatic scheduled processing
      query = query
        .eq('processing_status', 'scheduled')
        .lte('processing_scheduled_for', new Date().toISOString());
    }

    const { data: jobsToProcess, error: jobsError } = await query;

    if (jobsError) {
      console.error('Error fetching jobs to process:', jobsError);
      return new Response(
        JSON.stringify({ error: 'Failed to fetch jobs', details: jobsError }),
        { status: 500, headers: { 'Content-Type': 'application/json' } }
      );
    }

    if (!jobsToProcess || jobsToProcess.length === 0) {
      console.log('No jobs to process at this time');
      return new Response(
        JSON.stringify({ message: 'No jobs to process', processed: 0 }),
        { status: 200, headers: { 'Content-Type': 'application/json' } }
      );
    }

    console.log(`Found ${jobsToProcess.length} job(s) to process`);

    const results = [];

    // Process each job
    for (const job of jobsToProcess) {
      try {
        console.log(`Processing job ${job.id} (${job.job_name})...`);

        // Set status to 'running'
        await supabase
          .from('jobs')
          .update({ processing_status: 'running' })
          .eq('id', job.id);

        // Fetch baseline scope document (most recent)
        const { data: scopeDoc } = await supabase
          .from('scope_documents')
          .select('summary, raw_content')
          .eq('job_id', job.id)
          .order('created_at', { ascending: false })
          .limit(1)
          .single();

        const baselineScope = scopeDoc?.summary || scopeDoc?.raw_content || null;
        console.log('Baseline scope found:', !!baselineScope);

        // Fetch chat messages
        let messagesQuery = supabase
          .from('chat_messages')
          .select('*')
          .eq('job_id', job.id)
          .order('created_at', { ascending: true });

        if (job.last_processed_at) {
          messagesQuery = messagesQuery.gt('created_at', job.last_processed_at);
        }

        const { data: messages, error: messagesError } = await messagesQuery;

        if (messagesError) throw messagesError;

        if (!messages || messages.length === 0) {
          console.log(`No new messages for job ${job.id}`);

          // If we have a baseline scope but no job_overview, generate one
          if (baselineScope && !job.job_overview) {
            console.log('Generating initial overview from baseline scope...');
            const overviewResult = await updateJobOverview(
              baselineScope,
              null,
              [],
              [],
              job,
              job.job_name
            );

            await supabase
              .from('jobs')
              .update({
                job_overview: overviewResult.overview,
                ai_changelog: [...(job.ai_changelog || []), ...overviewResult.changes],
                processing_status: 'idle',
                processing_scheduled_for: null,
                last_processed_at: new Date().toISOString(),
              })
              .eq('id', job.id);

            results.push({
              job_id: job.id,
              status: 'success',
              reason: 'Generated initial overview from scope',
            });
          } else {
            await supabase
              .from('jobs')
              .update({
                processing_status: 'idle',
                processing_scheduled_for: null,
              })
              .eq('id', job.id);

            results.push({
              job_id: job.id,
              status: 'skipped',
              reason: 'no_new_messages',
            });
          }
          continue;
        }

        console.log(`Processing ${messages.length} message(s) for job ${job.id}`);

        // STEP 1: Classify messages
        const classification = await classifyMessages(messages, job.job_name);
        console.log('Classification complete');
        console.log('Overview relevant count:', classification.overview_relevant.length);
        console.log('New issues count:', classification.new_issues.length);
        console.log('New questions count:', classification.new_questions.length);
        console.log('New actions count:', classification.new_actions.length);

        // STEP 2: Run updaters
        const allChanges: any[] = [];
        const allCompletedItems: any[] = [];

        // Job Overview - pass baseline scope and all messages as fallback
        const overviewResult = await updateJobOverview(
          baselineScope,
          job.job_overview,
          classification.overview_relevant,
          messages,
          job,
          job.job_name
        );
        allChanges.push(...overviewResult.changes);

        // Open Issues
        const issuesResult = updateOpenIssues(
          job.open_issues,
          classification.new_issues,
          classification.resolved_issues
        );
        allChanges.push(...issuesResult.changes);
        allCompletedItems.push(...issuesResult.completedItems);

        // Questions
        const questionsResult = updateQuestions(
          job.unanswered_questions,
          job.resolved_questions,
          classification.new_questions,
          classification.answered_questions
        );
        allChanges.push(...questionsResult.changes);

        // Actions
        const actionsResult = updateActions(
          job.next_actions,
          classification.new_actions,
          classification.completed_actions
        );
        allChanges.push(...actionsResult.changes);
        allCompletedItems.push(...actionsResult.completedItems);

        // Risks
        const risksResult = updateRisks(
          job.warnings_and_risks,
          classification.new_risks
        );
        allChanges.push(...risksResult.changes);

        // Timelines
        const timelinesResult = updateTimelines(
          job.deadlines_and_timelines,
          classification.timeline_changes
        );
        allChanges.push(...timelinesResult.changes);

        // Dependencies
        const dependenciesResult = updateDependencies(
          job.dependencies,
          classification.new_dependencies,
          classification.cleared_dependencies
        );
        allChanges.push(...dependenciesResult.changes);
        allCompletedItems.push(...dependenciesResult.completedItems);

        // Merge completed items
        const updatedCompletedItems = [
          ...(job.completed_items || []),
          ...allCompletedItems,
        ];

        // STEP 3: Append to changelog
        const updatedChangelog = [
          ...(job.ai_changelog || []),
          ...allChanges,
        ];

        console.log('Saving updated state...');
        console.log('New overview length:', overviewResult.overview?.length || 0);
        console.log('Total changes:', allChanges.length);

        // Save updated state
        const { error: updateError } = await supabase
          .from('jobs')
          .update({
            job_overview: overviewResult.overview,
            open_issues: issuesResult.issues,
            unanswered_questions: questionsResult.unanswered,
            resolved_questions: questionsResult.resolved,
            next_actions: actionsResult.actions,
            completed_items: updatedCompletedItems,
            warnings_and_risks: risksResult.risks,
            deadlines_and_timelines: timelinesResult.timelines,
            dependencies: dependenciesResult.dependencies,
            ai_changelog: updatedChangelog,
            last_processed_at: new Date().toISOString(),
            processing_status: 'idle',
            processing_scheduled_for: null,
          })
          .eq('id', job.id);

        if (updateError) {
          console.error('Error updating job:', updateError);
          throw updateError;
        }

        console.log(`Successfully processed job ${job.id}`);
        results.push({
          job_id: job.id,
          job_name: job.job_name,
          status: 'success',
          messages_processed: messages.length,
          changes_made: allChanges.length,
          overview_length: overviewResult.overview?.length || 0,
        });
      } catch (jobError) {
        console.error(`Error processing job ${job.id}:`, jobError);
        await supabase
          .from('jobs')
          .update({ processing_status: 'failed' })
          .eq('id', job.id);
        results.push({
          job_id: job.id,
          status: 'error',
          error: jobError instanceof Error ? jobError.message : String(jobError),
        });
      }
    }

    console.log('Chat batch processing complete');
    return new Response(
      JSON.stringify({
        message: 'Processing complete',
        processed: results.length,
        results,
      }),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive',
        },
      }
    );
  } catch (error) {
    console.error('Fatal error in process-chat-batch:', error);
    return new Response(
      JSON.stringify({
        error: 'Fatal error',
        details: error instanceof Error ? error.message : String(error),
      }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Get OpenAI API key from environment
    const openaiApiKey = Deno.env.get('OPENAI_API_KEY');
    if (!openaiApiKey) {
      console.error('OPENAI_API_KEY not configured');
      throw new Error('OPENAI_API_KEY not configured');
    }

    console.log('OpenAI API key found, proceeding...');

    // Parse request body
    const { jobId, content, fileName, fileType, fileUri } = await req.json();

    if (!jobId || !content) {
      return new Response(
        JSON.stringify({ error: 'Missing required fields: jobId and content' }),
        {
          status: 400,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    console.log('Processing scope document for job:', jobId);
    console.log('Content length:', content.length);

    // Get authorization header
    const authHeader = req.headers.get('Authorization');
    if (!authHeader) {
      throw new Error('Missing authorization header');
    }

    // Create Supabase client with the user's auth token
    const supabaseUrl = Deno.env.get('SUPABASE_URL');
    const supabaseAnonKey = Deno.env.get('SUPABASE_ANON_KEY');
    const supabase = createClient(supabaseUrl, supabaseAnonKey, {
      global: {
        headers: { Authorization: authHeader },
      },
    });

    // Get the current user
    const { data: { user }, error: userError } = await supabase.auth.getUser();
    if (userError || !user) {
      console.error('Error getting user:', userError);
      throw new Error('Unauthorized');
    }

    console.log('User authenticated:', user.id);

    // Call OpenAI API to generate summary
    console.log('Calling OpenAI API...');
    const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${openaiApiKey}`,
      },
      body: JSON.stringify({
        model: 'gpt-4o-mini',
        messages: [
          {
            role: 'system',
            content: 'You are analyzing scope documents for industrial operations projects. Provide concise, high-level summaries that capture essential information at a glance. This summary will serve as the baseline for the job overview and will be maintained throughout the project lifecycle.',
          },
          {
            role: 'user',
            content: `Please provide a concise, high-level summary (TLDR) of this scope document. This will be the BASELINE summary for the job and will be preserved as the foundation of the job overview. Focus on:\n\n- Main objectives and deliverables\n- Key activities or work areas\n- Important stakeholders or teams\n- Critical timelines or milestones\n- Safety or compliance requirements\n\nKeep the summary professional, clear, and under 200 words. This is the foundation that will be maintained throughout the project.\n\nScope Document:\n${content}`,
          },
        ],
        max_tokens: 500,
        temperature: 0.7,
      }),
    });

    if (!openaiResponse.ok) {
      const errorText = await openaiResponse.text();
      console.error('OpenAI API error:', openaiResponse.status, errorText);
      throw new Error(`OpenAI API error: ${openaiResponse.status} - ${errorText}`);
    }

    const openaiData = await openaiResponse.json();
    const summary = openaiData.choices[0].message.content;

    console.log('Generated summary length:', summary.length);

    // Store in scope_documents table
    const { data: scopeDocument, error: insertError } = await supabase
      .from('scope_documents')
      .insert({
        job_id: jobId,
        raw_content: content,
        file_name: fileName || null,
        file_type: fileType || null,
        file_uri: fileUri || null,
        summary: summary,
        uploaded_by: user.id,
      })
      .select()
      .single();

    if (insertError) {
      console.error('Database insert error:', insertError);
      throw new Error(`Failed to store scope document: ${insertError.message}`);
    }

    console.log('Scope document stored successfully:', scopeDocument.id);

    // Update job_overview in jobs table - this is the BASELINE
    console.log('Updating job overview in jobs table with baseline scope...');
    const { error: updateError } = await supabase
      .from('jobs')
      .update({
        job_overview: summary,
        last_processed_at: new Date().toISOString(),
        processing_status: 'completed',
      })
      .eq('id', jobId);

    if (updateError) {
      console.error('Error updating job overview:', updateError);
      // Don't throw - the scope document was saved successfully
      console.warn('Scope document saved but job overview update failed');
    } else {
      console.log('Job overview updated successfully with baseline scope');
    }

    // Add changelog entry
    const changelogEntry = {
      timestamp: new Date().toISOString(),
      section: 'job_overview',
      change_type: 'created',
      new_value: summary,
      reason: 'Initial scope document processed - baseline established',
      metadata: {
        document_id: scopeDocument.id,
        file_name: fileName || 'Manual entry',
        content_length: content.length,
        is_baseline: true,
      },
    };

    // Fetch current changelog
    const { data: jobData } = await supabase
      .from('jobs')
      .select('ai_changelog')
      .eq('id', jobId)
      .single();

    const currentChangelog = jobData?.ai_changelog || [];
    const updatedChangelog = [changelogEntry, ...currentChangelog];

    // Update changelog
    await supabase
      .from('jobs')
      .update({ ai_changelog: updatedChangelog })
      .eq('id', jobId);

    console.log('Changelog updated with baseline entry');

    return new Response(
      JSON.stringify({
        success: true,
        summary: summary,
        documentId: scopeDocument.id,
        jobOverviewUpdated: !updateError,
        message: 'Scope document processed and baseline overview established',
      }),
      {
        status: 200,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  } catch (error) {
    console.error('Error processing scope:', error);
    return new Response(
      JSON.stringify({
        error: error.message || 'Internal server error',
      }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'npm:@supabase/supabase-js@2.84.0';

const SUPABASE_URL = Deno.env.get('SUPABASE_URL');
const SUPABASE_SERVICE_KEY = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY');
const OPENAI_API_KEY = Deno.env.get('OPENAI_API_KEY');

const supabase = createClient(SUPABASE_URL, SUPABASE_SERVICE_KEY);

console.info('process-chat-batch function started');

// STEP 1: CLASSIFIER - FIXED TO PRODUCE FLAT STRUCTURES
async function classifyMessages(messages: any[], jobContext: string) {
  console.log(`Classifying ${messages.length} messages...`);

  const messagesText = messages.map((msg, idx) => 
    `[${idx + 1}] (ID: ${msg.id}, User: ${msg.user_id}, Time: ${msg.created_at}) ${msg.message_text || '[Image/Voice message]'}`
  ).join('\n');

  const systemPrompt = `You are an AI assistant that analyzes construction/industrial job site chat messages.

Your task: Read chat messages and classify each one into categories. A single message can belong to MULTIPLE categories.

CATEGORIES (be LIBERAL - over-classify rather than miss things):

1. overview_relevant: ANY message with substantive job information (issues, progress, questions, updates, problems, discussions)
2. new_issues: Problems, blockers, broken equipment, things not working, complaints about site conditions
3. resolved_issues: Messages saying an issue is fixed, resolved, completed, working now
4. new_questions: Any question asked (even casual ones about the job)
5. answered_questions: Answers to questions (look for question + answer pairs)
6. new_actions: Tasks to do, action items, "need to", "should", "must", "going to"
7. completed_actions: Messages saying a task is done, finished, completed
8. new_risks: Warnings, potential problems, safety concerns, "might", "could go wrong", weather issues
9. timeline_changes: Schedule changes, delays, "running behind", "ahead of schedule", deadline mentions, inspection times
10. new_dependencies: Waiting on something (permits, other trades, client, equipment, materials, approvals)
11. cleared_dependencies: Messages saying they got what they were waiting for, dependency resolved
12. noise: ONLY pure small talk with zero job relevance ("good morning", "how's the family", "go sports team")

FIELD EXAMPLES:
- "pump is busted" → new_issues, overview_relevant
- "fixed the pump" → resolved_issues, overview_relevant, completed_actions
- "when does inspection happen?" → new_questions, overview_relevant
- "inspection is Tuesday at 2pm" → answered_questions, timeline_changes, overview_relevant
- "need to order more wire" → new_actions, overview_relevant
- "ordered the wire" → completed_actions, overview_relevant
- "might rain tomorrow" → new_risks, timeline_changes, overview_relevant
- "waiting on electrician" → new_dependencies, overview_relevant
- "electrician showed up" → cleared_dependencies, overview_relevant
- "hey man" → noise

OUTPUT FORMAT - Return a JSON object with these arrays. Each item MUST be a flat object with these EXACT fields:

{
  "overview_relevant": [
    {
      "message_id": "uuid",
      "text": "original message text"
    }
  ],
  "new_issues": [
    {
      "message_id": "uuid",
      "text": "original message text",
      "title": "short title (5-10 words)",
      "description": "detailed description"
    }
  ],
  "resolved_issues": [
    {
      "message_id": "uuid",
      "text": "original message text",
      "title": "what was resolved (5-10 words)",
      "description": "how it was resolved"
    }
  ],
  "new_questions": [
    {
      "message_id": "uuid",
      "text": "original message text",
      "question": "the question being asked"
    }
  ],
