              .from('chat_read_status')
              .upsert(
                {
                  user_id: session.user.id,
                  job_id: jobId,
                  last_read_at: new Date(),
                },
                { onConflict: 'user_id, job_id' }
              );

            if (error) {
              console.error('Error marking messages as read:', error);
            }
          }
        };

        markMessagesAsRead();
      }, [session?.user?.id, jobId]);

      // ... your chat tab code
    }
